fields.formula:
concat( 
  field("Type"), 
  "-", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)
